import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import './ChatScreen.css';
import backIcon from '../assets/back.png';
import groupIcon from '../assets/groups.png';

const socket = io('http://localhost:5001'); // Ensure a single socket instance

const ChatScreen = () => {
    const { exchangeName } = useParams(); 
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Join the exchange/room
        socket.emit('join', exchangeName);

        // Ensure only one event listener is attached
        const handleMessage = (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        };

        socket.on('message', handleMessage);

        return () => {
            // Cleanup listener to prevent duplication
            socket.off('message', handleMessage);
        };
    }, [exchangeName]);

    const handleSendMessage = () => {
        if (newMessage.trim() !== '') {
            socket.emit('sendMessage', { exchangeName, message: newMessage });
            setMessages((prevMessages) => [...prevMessages, newMessage]);
            setNewMessage('');
        }
    };

    const handleBack = () => {
        navigate('/rooms');
    };

    return (
        <div className="chat-screen">
            <div className="chat-header">
                <img src={backIcon} alt="Back" className="back-icon" onClick={handleBack} />
                <div className="chat-info">
                    <img src={groupIcon} alt="Group Icon" className="group-icon" />
                    <h2 className="room-name">{exchangeName}</h2>
                </div>
                <div className="chat-actions">
                    <button className="call-button">ðŸ“ž</button>
                    <button className="video-button">ðŸ“¹</button>
                </div>
            </div>
            <div className="chat-messages">
                {messages.map((message, index) => (
                    <div key={index} className="message">
                        <p>{message}</p>
                        <span className="time">12:00</span>
                    </div>
                ))}
            </div>
            <div className="chat-input">
                <input
                    type="text"
                    placeholder="Type here ..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
                />
                <button onClick={handleSendMessage} className="send-button">Send</button>
            </div>
        </div>
    );
};

export default ChatScreen;
