import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import './ChatScreen.css';
import backIcon from '../assets/back.png';
import groupIcon from '../assets/group.png'; // Import group icon

const socket = io('http://localhost:5001');

const ChatScreen = () => {
    const { exchangeName } = useParams(); // Get exchangeName from URL
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const navigate = useNavigate(); // Hook to navigate between pages

    const messageHandlerRef = useRef(); // Using a ref to avoid event listener duplication

    useEffect(() => {
        socket.emit('join', exchangeName);

        // Define the message handler
        messageHandlerRef.current = (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        };

        // Register the message handler
        socket.on('message', messageHandlerRef.current);

        return () => {
            // Cleanup the event listener when component unmounts or exchangeName changes
            socket.off('message', messageHandlerRef.current);
        };
    }, [exchangeName]);

    const handleSendMessage = () => {
        if (newMessage.trim() !== '') {
            // Emit the message to the server
            socket.emit('sendMessage', { exchangeName, message: newMessage });
            setMessages((prevMessages) => [...prevMessages, newMessage]); // Add the message to the local state
            setNewMessage(''); // Clear the input field after sending the message
        }
    };

    const handleBack = () => {
        navigate('/rooms'); // Navigate back to the RoomsPage
    };

    return (
        <div className="chat-screen">
            <div className="chat-header">
                <img src={backIcon} alt="Back" className="back-icon" onClick={handleBack} />
                <div className="chat-info">
                    <img src={groupIcon} alt="Group Icon" className="group-icon" />
                    <h2 className="room-name">{exchangeName}</h2>
                </div>
                <div className="chat-actions">
                    <button className="call-button">ðŸ“ž</button>
                    <button className="video-button">ðŸ“¹</button>
                </div>
            </div>
            <div className="chat-messages">
                {messages.map((message, index) => (
                    <div key={index} className="message">
                        <p>{message}</p>
                        <span className="time">12:00</span> {/* Replace with actual time */}
                    </div>
                ))}
            </div>
            <div className="chat-input">
                <input
                    type="text"
                    placeholder="Type here ..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()} // Handle "Enter" key press
                />
                <button onClick={handleSendMessage} className="send-button">Send</button>
            </div>
        </div>
    );
};

export default ChatScreen;
