import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './RoomsPage.css';
import groupsIcon from '../assets/group.png';
import backIcon from '../assets/back.png'; // Import the back icon
import deleteIcon from '../assets/delete.png'; // Import the delete icon

const RoomsPage = () => {
    const [rooms, setRooms] = useState([]);
    const [showCreateRoom, setShowCreateRoom] = useState(false);
    const [newRoomName, setNewRoomName] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchRooms();
    }, []);

    const fetchRooms = () => {
        fetch('http://localhost:5001/exchanges')
            .then(response => response.json())
            .then(data => setRooms(data))
            .catch(error => console.error('Error fetching exchange names:', error));
    };

    const handleRoomClick = (exchangeName) => {
        navigate(`/chat/${exchangeName}`);
    };

    const handleCreateRoom = () => {
        if (newRoomName.trim() === '') {
            alert('Please enter a room name.');
            return;
        }

        fetch('http://localhost:5001/create-exchange', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ exchangeName: newRoomName }),
        })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    setRooms([...rooms, newRoomName]);
                    setNewRoomName('');
                    setShowCreateRoom(false);
                } else {
                    alert('Failed to create room. Please try again.');
                }
            })
            .catch(error => console.error('Error creating exchange:', error));
    };

    const handleDeleteRoom = (exchangeName) => {
        const confirmDelete = window.confirm(`Are you sure you want to delete the room "${exchangeName}"?`);
        if (confirmDelete) {
            // Optimistic UI update: Remove the room immediately from the UI
            setRooms(rooms.filter(room => room !== exchangeName));

            fetch(`http://localhost:5001/delete-exchange`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ exchangeName }),
            })
                .then(response => response.json())
                .then(data => {
                    if (!data.success) {
                        alert('Failed to delete room. Please try again.');
                        // Revert UI update if deletion failed
                        fetchRooms();
                    }
                })
                .catch(error => {
                    console.error('Error deleting exchange:', error);
                    // Revert UI update if an error occurred
                    fetchRooms();
                });
        }
    };

    const handleBackClick = () => {
        navigate('/chats'); // Navigate directly to the ChatListPage
    };

    return (
        <div className="rooms-page">
            <img src={backIcon} alt="Back" className="back-room-icon" onClick={handleBackClick} /> {/* Add the back button */}
            <h1>Available Rooms To Join!</h1>
            <ul className="rooms-list">
                {rooms.map((room, index) => (
                    <li key={index} className="room-item">
                        <img src={groupsIcon} alt={room} className="room-icon" onClick={() => handleRoomClick(room)} />
                        <span onClick={() => handleRoomClick(room)}>{room}</span>
                        <img
                            src={deleteIcon}
                            alt="Delete"
                            className="delete-icon"
                            onClick={() => handleDeleteRoom(room)}
                        />
                    </li>
                ))}
            </ul>
            {showCreateRoom && (
                <div className="create-room-container">
                    <input
                        type="text"
                        placeholder="Enter room name"
                        value={newRoomName}
                        onChange={(e) => setNewRoomName(e.target.value)}
                        className="new-room-input"
                    />
                    <button onClick={handleCreateRoom} className="create-room-button">
                        Create Room
                    </button>
                </div>
            )}
            <button
                className="proceed-button"
                onClick={() => setShowCreateRoom(!showCreateRoom)}
            >
                {showCreateRoom ? 'Cancel' : 'Create Room'}
            </button>
        </div>
    );
};

export default RoomsPage;
