import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProfileEditPage.css';
import profilePic from '../assets/profile.png'; // Profile picture placeholder
import backIcon from '../assets/back.png'; // Back icon
import checkIcon from '../assets/check.png'; // Check icon

const ProfileEditPage = () => {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [username, setUsername] = useState('');
  const [phone, setPhone] = useState('');
  const [bio, setBio] = useState('');
  const [theme, setTheme] = useState('light');

  useEffect(() => {
    // Load user data from localStorage
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setName(storedUser.name);
      setUsername(storedUser.username);
      setPhone(storedUser.phone);
      setBio(storedUser.bio);
    }
  }, []);

  const handleBack = () => {
    navigate('/chats');
  };

  const handleSave = () => {
    // Save user data to localStorage
    const userData = { name, username, phone, bio };
    localStorage.setItem('user', JSON.stringify(userData));
    navigate('/chats');
  };

  const handleThemeChange = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
    // Implement theme change functionality
  };

  return (
    <div className="profile-edit-page">
      <div className="top-bar">
        <img src={backIcon} alt="Back" className="back-icon" onClick={handleBack} />
        <h2 className="title">Edit Profile</h2>
        <img src={checkIcon} alt="Save" className="check-icon" onClick={handleSave} />
      </div>
      <div className="profile-pic-container">
        <img src={profilePic} alt="Profile" className="profile-pic" />
        <div className="camera-icon">ðŸ“·</div>
      </div>
      <div className="input-container">
        <label>Name</label>
        <input 
          type="text" 
          placeholder="Your Username..." 
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div className="input-container">
        <label>User Name</label>
        <input 
          type="text" 
          value={username}
          readOnly // Making the username field read-only
        />
      </div>
      <div className="input-container">
        <label>Change Number</label>
        <input 
          type="text" 
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
      </div>
      <div className="input-container">
        <label>Biography</label>
        <textarea 
          value={bio}
          onChange={(e) => setBio(e.target.value)}
        />
      </div>
      <div className="theme-toggle">
        <button onClick={handleThemeChange}>
          {theme === 'light' ? 'Switch to Dark Theme' : 'Switch to Light Theme'}
        </button>
      </div>
    </div>
  );
};

export default ProfileEditPage;
