import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AuthPage.css';
import eyeIconOpen from '../assets/eye-open.png';  // Path to the eye open icon
import eyeIconClosed from '../assets/eye-closed.png'; // Path to the eye closed icon

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [isForgotPassword, setIsForgotPassword] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [retypePassword, setRetypePassword] = useState('');
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showRetypePassword, setShowRetypePassword] = useState(false);
  const navigate = useNavigate();

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const toggleRetypePasswordVisibility = () => {
    setShowRetypePassword(!showRetypePassword);
  };

  const handleLogin = () => {
    const users = JSON.parse(localStorage.getItem('users')) || {};
    if (users[username] && users[username].password === password) {
      localStorage.setItem('activeUser', username);
      navigate('/chats'); // Redirect to the chat list page
    } else {
      setError('Invalid username or password');
    }
  };

  const handleSignUp = () => {
    if (password !== retypePassword) {
      setError('Passwords do not match');
      return;
    }

    const users = JSON.parse(localStorage.getItem('users')) || {};
    if (users[username]) {
      setError('Username already exists');
      return;
    }

    users[username] = { password };
    localStorage.setItem('users', JSON.stringify(users));
    alert('Signup successful! Please login with your credentials.');
    setIsLogin(true);
    setUsername('');
    setPassword('');
    setRetypePassword('');
    setError('');
  };

  const handleForgotPassword = () => {
    const users = JSON.parse(localStorage.getItem('users')) || {};
    if (users[username]) {
      users[username].password = password;
      localStorage.setItem('users', JSON.stringify(users));
      alert('Password reset successful! Please login with your new password.');
      setIsForgotPassword(false);
      setIsLogin(true);
      setUsername('');
      setPassword('');
      setError('');
    } else {
      setError('Username does not exist');
    }
  };

  return (
    <div className="auth-container">
      {isLogin && !isForgotPassword && (
        <div className="login-container">
          <h2>Login</h2>
          {error && <p className="error">{error}</p>}
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <div className="password-container">
            <input
              type={showPassword ? 'text' : 'password'}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <img
              src={showPassword ? eyeIconClosed : eyeIconOpen}
              alt="Toggle Password"
              className="toggle-password"
              onClick={togglePasswordVisibility}
            />
          </div>
          <button onClick={handleLogin}>Login</button>
          <button onClick={() => setIsLogin(false)}>New User? Sign Up</button>
          <button onClick={() => setIsForgotPassword(true)}>Forgot Password</button>
        </div>
      )}

      {!isLogin && (
        <div className="signup-container">
          <h2>Sign Up</h2>
          {error && <p className="error">{error}</p>}
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <div className="password-container">
            <input
              type={showPassword ? 'text' : 'password'}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <img
              src={showPassword ? eyeIconClosed : eyeIconOpen}
              alt="Toggle Password"
              className="toggle-password"
              onClick={togglePasswordVisibility}
            />
          </div>
          <div className="password-container">
            <input
              type={showRetypePassword ? 'text' : 'password'}
              placeholder="Retype Password"
              value={retypePassword}
              onChange={(e) => setRetypePassword(e.target.value)}
            />
            <img
              src={showRetypePassword ? eyeIconClosed : eyeIconOpen}
              alt="Toggle Password"
              className="toggle-password"
              onClick={toggleRetypePasswordVisibility}
            />
          </div>
          <div className="terms-container">
            <input type="checkbox" id="terms" />
            <label htmlFor="terms">I agree to the terms & conditions</label>
          </div>
          <button onClick={handleSignUp}>Sign Up</button>
          <button onClick={() => setIsLogin(true)}>Back to Login</button>
        </div>
      )}

      {isForgotPassword && (
        <div className="forgot-password-container">
          <h2>Forgot Password</h2>
          {error && <p className="error">{error}</p>}
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <div className="password-container">
            <input
              type={showPassword ? 'text' : 'password'}
              placeholder="New Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <img
              src={showPassword ? eyeIconClosed : eyeIconOpen}
              alt="Toggle Password"
              className="toggle-password"
              onClick={togglePasswordVisibility}
            />
          </div>
          <button onClick={handleForgotPassword}>Reset Password</button>
          <button onClick={() => { setIsForgotPassword(false); setIsLogin(true); }}>Back to Login</button>
        </div>
      )}
    </div>
  );
};

export default AuthPage;
