import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProfileEditPage.css';
import profilePic from '../assets/profile.png'; // Profile picture placeholder
import backIcon from '../assets/back.png'; // Back icon
import checkIcon from '../assets/check.png'; // Check icon

const ProfileEditPage = ({ user }) => {
  const navigate = useNavigate();
  const [name, setName] = useState(user ? user.name : '');
  const [username, setUsername] = useState(user ? user.username : '@xyz_0123');
  const [phone, setPhone] = useState(user ? user.phone : '+61 xxx xxx xxx');
  const [bio, setBio] = useState(user ? user.bio : 'ui/ux designer, software engineer, available to work overtime');
  const [theme, setTheme] = useState('light');

  useEffect(() => {
    if (user) {
      setName(user.name);
      setUsername(user.username);
      setPhone(user.phone);
      setBio(user.bio);
    }
  }, [user]);

  const handleBack = () => {
    navigate(-1);
  };

  const handleSave = () => {
    // Implement save functionality
    navigate(-1);
  };

  const handleThemeChange = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
    // Implement theme change functionality
  };

  return (
    <div className="profile-edit-page">
      <div className="top-bar">
        <img src={checkIcon} alt="Save" className="check-icon" onClick={handleSave} />
        <h2 className="title">Edit Profile</h2>
        <img src={backIcon} alt="Back" className="back-icon" onClick={handleBack} />
      </div>
      <div className="profile-pic-container">
        <img src={profilePic} alt="Profile" className="profile-pic" />
        <div className="camera-icon">ðŸ“·</div>
      </div>
      <div className="input-container">
        <label>Name</label>
        <input 
          type="text" 
          placeholder="Your Username..." 
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div className="input-container">
        <label>User Name</label>
        <input 
          type="text" 
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          disabled // Making the username field read-only
        />
      </div>
      <div className="input-container">
        <label>Change Number</label>
        <input 
          type="text" 
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
      </div>
      <div className="input-container">
        <label>Biography</label>
        <textarea 
          value={bio}
          onChange={(e) => setBio(e.target.value)}
        />
      </div>
      <div className="theme-toggle">
        <label>Theme</label>
        <button onClick={handleThemeChange}>
          {theme === 'light' ? 'Switch to Dark Theme' : 'Switch to Light Theme'}
        </button>
      </div>
    </div>
  );
};

export default ProfileEditPage;
