import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './RoomsPage.css';
import groupsIcon from '../assets/group.png';

const RoomsPage = () => {
    const [rooms, setRooms] = useState([]);
    const [showCreateRoom, setShowCreateRoom] = useState(false); // State to toggle the display of the input and button
    const [newRoomName, setNewRoomName] = useState(''); // State for new room name
    const navigate = useNavigate();

    useEffect(() => {
        fetch('http://localhost:5001/exchanges')
            .then(response => response.json())
            .then(data => setRooms(data))
            .catch(error => console.error('Error fetching exchange names:', error));
    }, []);

    const handleRoomClick = (exchangeName) => {
        navigate(`/chat/${exchangeName}`);
    };

    const handleCreateRoom = () => {
        if (newRoomName.trim() === '') {
            alert('Please enter a room name.');
            return;
        }

        fetch('http://localhost:5001/create-exchange', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ exchangeName: newRoomName }),
        })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    setRooms([...rooms, newRoomName]); // Add the new room to the list
                    setNewRoomName(''); // Clear the input field
                    setShowCreateRoom(false); // Hide the create room fields
                } else {
                    alert('Failed to create room. Please try again.');
                }
            })
            .catch(error => console.error('Error creating exchange:', error));
    };

    return (
        <div className="rooms-page">
            <h1>Available Groups To Join!</h1>
            <ul className="rooms-list">
                {rooms.map((room, index) => (
                    <li key={index} className="room-item" onClick={() => handleRoomClick(room)}>
                        <img src={groupsIcon} alt={room} className="room-icon" />
                        <span>{room}</span>
                    </li>
                ))}
            </ul>
            {showCreateRoom && (
                <div className="create-room-container">
                    <input
                        type="text"
                        placeholder="Enter room name"
                        value={newRoomName}
                        onChange={(e) => setNewRoomName(e.target.value)}
                        className="new-room-input"
                    />
                    <button onClick={handleCreateRoom} className="create-room-button">
                        Create Room
                    </button>
                </div>
            )}
            <button
                className="proceed-button"
                onClick={() => setShowCreateRoom(!showCreateRoom)} // Toggle the visibility of the create room fields
            >
                {showCreateRoom ? 'Cancel' : 'Create Room'}
            </button>
        </div>
    );
};

export default RoomsPage;
