import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import './ChatScreen.css';
import backIcon from '../assets/back.png';
import groupIcon from '../assets/group.png';
import sendIcon from '../assets/send.png'; // Import the send icon
import audioIcon from '../assets/audio.png'; // Import the audio icon
import videoIcon from '../assets/video.png'; // Import the video icon

// Ensure the socket connection is not duplicated
const socket = io('http://localhost:5001');

const ChatScreen = () => {
    const { exchangeName } = useParams();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const navigate = useNavigate();

    const messageSet = useRef(new Set()); // Use a set to store unique messages

    useEffect(() => {
        console.log(`Joining exchange: ${exchangeName}`);
        socket.emit('join', exchangeName);

        const handleMessage = (message) => {
            console.log('Received message:', message);

            // Only add the message if it's not already in the set
            if (!messageSet.current.has(message)) {
                messageSet.current.add(message);
                setMessages((prevMessages) => [...prevMessages, message]);
            }
        };

        socket.on('message', handleMessage);

        return () => {
            console.log(`Cleaning up listener for: ${exchangeName}`);
            socket.off('message', handleMessage);
        };
    }, [exchangeName]);

    const handleSendMessage = () => {
        if (newMessage.trim()) {
            console.log(`Sending message: ${newMessage}`);
            socket.emit('sendMessage', { exchangeName, message: newMessage });
            // Add the new message to the list and set
            if (!messageSet.current.has(newMessage)) {
                messageSet.current.add(newMessage);
                setMessages((prevMessages) => [...prevMessages, newMessage]);
            }
            setNewMessage('');
        }
    };

    const handleBack = () => {
        navigate('/rooms');
    };

    return (
        <div className="chat-screen">
            <div className="chat-header">
                <img src={backIcon} alt="Back" className="chat-back-icon" onClick={handleBack} />
                <div className="chat-info">
                    <img src={groupIcon} alt="Group Icon" className="group-icon" />
                    <h2 className="room-name">{exchangeName}</h2>
                </div>
                <div className="chat-actions">
                    <img src={audioIcon} alt="Audio Call" className="audio-icon" />
                    <img src={videoIcon} alt="Video Call" className="video-icon" />
                </div>
            </div>
            <div className="chat-messages">
                {messages.map((message, index) => (
                    <div key={index} className="message">
                        <p>{message}</p>
                        <span className="time">12:00</span>
                    </div>
                ))}
            </div>
            <div className="chat-input">
                <input
                    type="text"
                    placeholder="Type here ..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
                />
                <button onClick={handleSendMessage} className="send-button">
                    <img src={sendIcon} alt="Send" className="send-icon" />
                </button>
            </div>
        </div>
    );
};

export default ChatScreen;
