import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import profilePic from '../assets/profile.png';
import checkIcon from '../assets/check.png';
import backIcon from '../assets/back.png';
import './ProfileEditPage.css';

const ProfileEditPage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [bio, setBio] = useState('');
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');

  useEffect(() => {
    const activeUser = localStorage.getItem('activeUser');
    if (activeUser) {
      setUsername(activeUser);
      const users = JSON.parse(localStorage.getItem('users')) || {};
      if (users[activeUser]) {
        setName(activeUser); // Setting the name to be the username by default
        setPhone(users[activeUser].phone || '');
        setBio(users[activeUser].bio || '');
      }
    }
  }, []);

  useEffect(() => {
    document.body.className = theme;
    localStorage.setItem('theme', theme);
  }, [theme]);

  const handleSave = () => {
    const users = JSON.parse(localStorage.getItem('users')) || {};
    if (username && users[username]) {
      users[username] = { ...users[username], phone, bio };
      localStorage.setItem('users', JSON.stringify(users));
      alert('Profile updated successfully!');
      navigate('/chats');
    }
  };

  const handleThemeChange = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <div className={`profile-edit-container ${theme}`}>
      <div className="top-bar">
        <img src={backIcon} alt="Back" className="back-icon" onClick={() => navigate('/chats')} />
        <h2>Edit Profile</h2>
        <img src={checkIcon} alt="Save" className="check-icon" onClick={handleSave} />
      </div>
      <div className="profile-pic-container">
        <img src={profilePic} alt="Profile" className="profile-pic" />
        <div className="camera-icon">ðŸ“·</div>
      </div>
      <div className="input-container">
        <label>Name</label>
        <input 
          type="text" 
          placeholder="Your Username..." 
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div className="input-container">
        <label>User Name</label>
        <input 
          type="text" 
          value={username}
          readOnly // Making the username field read-only
        />
      </div>
      <div className="input-container">
        <label>Change Number</label>
        <input 
          type="text" 
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
      </div>
      <div className="input-container">
        <label>Biography</label>
        <textarea 
          value={bio}
          onChange={(e) => setBio(e.target.value)}
        />
      </div>
      <div className="theme-toggle">
        <button onClick={handleThemeChange}>
          {theme === 'light' ? 'Switch to Dark Theme' : 'Switch to Light Theme'}
        </button>
      </div>
    </div>
  );
};

export default ProfileEditPage;
