import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AuthPage.css';

const AuthPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    // Mock user data based on successful login
    const user = {
      name: 'Yashin', // Replace with actual data from your login logic
      username: username,
      phone: '+61 xxx xxx xxx', // Replace with actual data
      bio: 'ui/ux designer, software engineer, available to work overtime', // Replace with actual data
    };

    // Save user data to localStorage
    localStorage.setItem('user', JSON.stringify(user));

    // Navigate to ChatListPage with user data
    navigate('/chats');
  };

  return (
    <div className="auth-page">
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
      <button onClick={() => navigate('/signup')}>New User? Sign Up</button>
      <button onClick={() => navigate('/forgot-password')}>Forgot Password</button>
    </div>
  );
};

export default AuthPage;
