import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import './ChatScreen.css';
import backIcon from '../assets/back.png';
import groupIcon from '../assets/group.png';
import sendIcon from '../assets/send.png';
import audioIcon from '../assets/audio.png';
import videoIcon from '../assets/video.png';
import attachmentIcon from '../assets/attachment.png';

const socket = io('http://localhost:5001');

const ChatScreen = () => {
    const { exchangeName } = useParams();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const navigate = useNavigate();

    const messageSet = useRef(new Set());
    const username = localStorage.getItem('activeUser');

    useEffect(() => {
        socket.emit('join', exchangeName, username);

        const handleMessage = (message) => {
            if (!messageSet.current.has(message)) {
                messageSet.current.add(message);
                setMessages((prevMessages) => [...prevMessages, message]);
            }
        };

        socket.on('message', handleMessage);

        return () => {
            socket.emit('leave', exchangeName, username);
            socket.off('message', handleMessage);
        };
    }, [exchangeName, username]);

    const handleSendMessage = () => {
        if (newMessage.trim()) {
            const timestamp = new Date().getTime();
            const messageToSend = `${username}: ${newMessage} ${timestamp}`;
            socket.emit('sendMessage', { exchangeName, message: messageToSend });
            if (!messageSet.current.has(messageToSend)) {
                messageSet.current.add(messageToSend);
                setMessages((prevMessages) => [...prevMessages, messageToSend]);
            }
            setNewMessage('');
        }
    };

    const handleBack = () => {
        navigate('/rooms');
    };

    // Handle double-click to make text bold
    const handleDoubleClick = () => {
        setNewMessage(prevMessage => `**${prevMessage}**`);
    };

    return (
        <div className="chat-screen">
            <div className="chat-header">
                <img src={backIcon} alt="Back" className="chat-back-icon" onClick={handleBack} />
                <div className="chat-info">
                    <img src={groupIcon} alt="Group Icon" className="group-icon" />
                    <h2 className="room-name">{exchangeName}</h2>
                </div>
                <div className="chat-actions">
                    <img src={videoIcon} alt="Video Call" className="video-icon" />
                    <img src={audioIcon} alt="Audio Call" className="audio-icon" />
                </div>
            </div>
            <div className="chat-messages">
                {messages.map((message, index) => {
                    const isOwnMessage = message.startsWith(`${username}:`);
                    const messageContent = message.substring(0, message.lastIndexOf(' '));
                    return (
                        <div
                            key={index}
                            className={`message ${isOwnMessage ? 'sent' : 'received'}`}
                        >
                            <p dangerouslySetInnerHTML={{ __html: messageContent.replace(/\*\*(.*?)\*\*/g, '<b>$1</b>') }}></p>
                        </div>
                    );
                })}
            </div>
            <div className="chat-input">
                <div className="chat-input-container">
                    <input
                        type="text"
                        placeholder="Type here ..."
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        onDoubleClick={handleDoubleClick} // Add double-click event
                        onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
                    />
                    <img src={attachmentIcon} alt="Attach" className="attachment-icon" />
                    <button onClick={handleSendMessage} className="send-button">
                        <img src={sendIcon} alt="Send" className="send-icon" />
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ChatScreen;
