import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import './ChatScreen.css';
import backIcon from '../assets/back.png';
import groupIcon from '../assets/group.png';
import sendIcon from '../assets/send.png'; 
import audioIcon from '../assets/audio.png'; 
import videoIcon from '../assets/video.png'; 

const socket = io('http://localhost:5001');

const ChatScreen = () => {
    const { exchangeName } = useParams();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const navigate = useNavigate();

    const messageSet = useRef(new Set());
    const currentUser = localStorage.getItem('activeUser');

    useEffect(() => {
        socket.emit('join', { exchangeName, username: currentUser });

        const handleMessage = (messageData) => {
            const { message, username } = messageData;
            if (!messageSet.current.has(message)) {
                messageSet.current.add(message);
                setMessages((prevMessages) => [...prevMessages, { message, username }]);
            }
        };

        socket.on('message', handleMessage);

        return () => {
            socket.off('message', handleMessage);
        };
    }, [exchangeName, currentUser]);

    const handleSendMessage = () => {
        if (newMessage.trim()) {
            const messageData = { exchangeName, message: newMessage, username: currentUser };
            socket.emit('sendMessage', messageData);
            if (!messageSet.current.has(newMessage)) {
                messageSet.current.add(newMessage);
                setMessages((prevMessages) => [...prevMessages, { message: newMessage, username: currentUser }]);
            }
            setNewMessage('');
        }
    };

    const handleBack = () => {
        navigate('/rooms');
    };

    return (
        <div className="chat-screen">
            <div className="chat-header">
                <img src={backIcon} alt="Back" className="chat-back-icon" onClick={handleBack} />
                <div className="chat-info">
                    <img src={groupIcon} alt="Group Icon" className="group-icon" />
                    <h2 className="room-name">{exchangeName}</h2>
                </div>
                <div className="chat-actions">
                    <img src={audioIcon} alt="Audio Call" className="audio-icon" />
                    <img src={videoIcon} alt="Video Call" className="video-icon" />
                </div>
            </div>
            <div className="chat-messages">
                {messages.map((msgData, index) => (
                    <div key={index} className={`message ${msgData.username === currentUser ? 'sent' : 'received'}`}>
                        <p>{msgData.username}: {msgData.message}</p>
                    </div>
                ))}
            </div>
            <div className="chat-input">
                <input
                    type="text"
                    placeholder="Type here ..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
                />
                <button onClick={handleSendMessage} className="send-button">
                    <img src={sendIcon} alt="Send" className="send-icon" />
                </button>
            </div>
        </div>
    );
};

export default ChatScreen;
