import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import './ChatScreen.css';
import backIcon from '../assets/back.png';
import groupIcon from '../assets/group.png'; // Import group icon

// Ensure socket is only initialized once globally
const socket = io('http://localhost:5001');

const ChatScreen = () => {
    const { exchangeName } = useParams(); 
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const navigate = useNavigate();

    const messageHandlerRef = useRef(); // Ref to keep the handler function

    useEffect(() => {
        console.log('Joining exchange:', exchangeName);
        socket.emit('join', exchangeName);

        // Prevent multiple listeners by ensuring the handler is defined only once
        if (!messageHandlerRef.current) {
            messageHandlerRef.current = (message) => {
                console.log('Received message:', message); // Debugging log
                setMessages((prevMessages) => [...prevMessages, message]);
            };
            socket.on('message', messageHandlerRef.current);
        }

        return () => {
            console.log('Cleaning up listener for:', exchangeName);
            // Ensure cleanup happens correctly to prevent duplicates
            socket.off('message', messageHandlerRef.current);
            messageHandlerRef.current = null;
        };
    }, [exchangeName]);

    const handleSendMessage = () => {
        if (newMessage.trim() !== '') {
            console.log('Sending message:', newMessage);
            socket.emit('sendMessage', { exchangeName, message: newMessage });
            setMessages((prevMessages) => [...prevMessages, newMessage]); 
            setNewMessage(''); 
        }
    };

    const handleBack = () => {
        navigate('/rooms'); 
    };

    return (
        <div className="chat-screen">
            <div className="chat-header">
                <img src={backIcon} alt="Back" className="back-icon" onClick={handleBack} />
                <div className="chat-info">
                    <img src={groupIcon} alt="Group Icon" className="group-icon" />
                    <h2 className="room-name">{exchangeName}</h2>
                </div>
                <div className="chat-actions">
                    <button className="call-button">📞</button>
                    <button className="video-button">📹</button>
                </div>
            </div>
            <div className="chat-messages">
                {messages.map((message, index) => (
                    <div key={index} className="message">
                        <p>{message}</p>
                        <span className="time">12:00</span> 
                    </div>
                ))}
            </div>
            <div className="chat-input">
                <input
                    type="text"
                    placeholder="Type here ..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()} 
                />
                <button onClick={handleSendMessage} className="send-button">Send</button>
            </div>
        </div>
    );
};

export default ChatScreen;
